import { getDeptList, saveDept } from '@/api/system';
import pick from 'lodash.pick';
export default {
  name: 'DeptModal',
  components: {},
  data: function data() {
    return {
      description: '列表使用场景：后台管理中的权限管理以及角色管理，可用于基于 RBAC 设计的角色权限控制，颗粒度细到每一个操作类型。',
      visible: false,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      depts: [{
        key: '0',
        value: '0',
        title: '无'
      }],
      mdl: {},
      confirmLoading: false,
      form: this.$form.createForm(this)
    };
  },
  beforeCreate: function beforeCreate() {},
  created: function created() {
    var _this = this;

    getDeptList().then(function (res) {
      _this.buildtree(res.rows, _this.depts, '0');
    });
  },
  methods: {
    add: function add(parentId) {
      this.form.resetFields();
      this.edit({
        parentId: parentId || '0'
      });
    },
    edit: function edit(record) {
      var _this2 = this;

      this.mdl = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this2.mdl.parentId += '';

        _this2.form.setFieldsValue(pick(_this2.mdl, 'deptId', 'parentId', 'leader', 'phone', 'status', 'email', 'orderNum', 'deptName', 'deptNo')); // this.form.setFieldsValue({ ...record })

      });
    },
    buildtree: function buildtree(list, arr, parentId) {
      var _this3 = this;

      list.forEach(function (item) {
        if (item.parentId === parentId) {
          var child = {
            key: item.deptId,
            value: item.deptId + '',
            title: item.deptName,
            children: []
          };

          _this3.buildtree(list, child.children, item.deptId);

          arr.push(child);
        }
      });
    },
    handleSubmit: function handleSubmit(e) {
      var _this4 = this;

      e.preventDefault();
      this.form.validateFields(function (err, values) {
        if (!err) {
          console.log('Received values of form: ', values);
          _this4.confirmLoading = true;
          saveDept(values).then(function (res) {
            if (res.code === 0) {
              _this4.$message.success('保存成功');

              _this4.$emit('ok');

              _this4.visible = false;
            } else {
              _this4.$message.warning(res.msg);
            }
          }).catch(function () {
            _this4.$message.error('系统错误，请稍后再试');
          }).finally(function () {
            _this4.confirmLoading = false;
          });
        }
      });
    }
  },
  watch: {
    /*
