import { getRoleAll, saveUser } from '@/api/system';
import pick from 'lodash.pick';
export default {
  name: 'UserModal',
  props: {
    deptTree: {
      type: Array,
      required: true
    }
  },
  components: {},
  data: function data() {
    return {
      description: '列表使用场景：后台管理中的权限管理以及角色管理，可用于基于 RBAC 设计的角色权限控制，颗粒度细到每一个操作类型。',
      visible: false,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      roleAll: [],
      mdl: {},
      deptCheck: true,
      form: this.$form.createForm(this)
    };
  },
  beforeCreate: function beforeCreate() {},
  created: function created() {
    this.loadRoleAll();
  },
  methods: {
    add: function add() {
      this.form.resetFields();
      this.edit({
        userId: 0,
        deptId: ''
      });
    },
    edit: function edit(record) {
      var _this = this;

      this.mdl = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.mdl, 'userId', 'loginName', 'userName', 'status', 'roleIds', 'remark', 'deptId', 'email', 'phonenumber', 'userType')); // this.form.setFieldsValue({ ...record })

      });
    },
    onChange: function onChange(value, label, extra) {
      if (extra.triggerNode.$children.length > 0) {
        this.$message.error('不能选择父节点' + "".concat(label));
        this.deptCheck = false;
      } else {
        this.deptCheck = true;
      }
    },
    loadRoleAll: function loadRoleAll() {
      var _this2 = this;

      getRoleAll().then(function (res) {
        _this2.roleAll = res.rows;
        console.log('roleALl', _this2.roleAll);
      });
    },
    handleSubmit: function handleSubmit(e) {
      var _this3 = this;

      e.preventDefault();

      if (!this.deptCheck) {
        this.$message.error('不能选择父节点');
        return;
      }

      this.form.validateFields(function (err, values) {
        if (!err) {
          console.log('Received values of form: ', values);
          _this3.confirmLoading = true;
          saveUser(values).then(function (res) {
            if (res.code === 0) {
              _this3.$message.success('保存成功');

              _this3.$emit('ok');

              _this3.visible = false;
            } else {
              _this3.$message.warning(res.msg);
            }
          }).catch(function () {
            _this3.$message.error('系统错误，请稍后再试');
          }).finally(function () {
            _this3.confirmLoading = false;
          });
        }
      });
    }
  },
  watch: {
    /*
      'selectedRows': function (selectedRows) {
