import { authDataScope, getRoleDeptIds, getDeptList } from '@/api/system'
import pick from 'lodash.pick'

export default {
  name: 'RoleModal',
  data () {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      },
      visible: false,
      confirmLoading: false,
      mdl: {},

      form: this.$form.createForm(this),
      deptTree: [],
      // 是否显示部门树
      dataScope: false,
      autoExpandParent: true,
      checkedKeys: []
    }
  },
  created () {
    getDeptList().then(res => {
      this.buildtree(res.rows, this.deptTree, '0')
    })
  },
  methods: {
    add () {
      this.form.resetFields()
      this.edit({ })
    },
    edit (record) {
      getRoleDeptIds(record.roleId).then(res => { this.checkedKeys = res || [] })
      this.mdl = Object.assign({}, record)
      this.dataScope = this.mdl.dataScope === '2'
      this.visible = true
      this.$nextTick(() => {
        this.form.setFieldsValue(pick(this.mdl, 'roleId', 'roleName', 'roleKey', 'dataScope'))
        // this.form.setFieldsValue(...record)
      })
    },
    close () {
      this.$emit('close')
      this.visible = false
    },
    buildtree (list, arr, parentId) {
      list.forEach(item => {
        if (item.parentId === parentId) {
          var child = {
            key: item.deptId + '',
            value: item.deptId + '',
            title: item.deptName,
            children: []
          }
          this.buildtree(list, child.children, item.deptId)
          if (child.children.length === 0) { delete child.children }
          arr.push(child)
        }
      })
    },
    scopeChange (value) {
      this.dataScope = value === '2'
    },
    handleOk (e) {
      const _this = this
      // 触发表单验证
      this.form.validateFields((err, values) => {
        // 验证表单没错误
        if (!err) {
          console.log(this.checkedKeys)

          values.deptIds = this.dataScope ? this.checkedKeys : []
          _this.confirmLoading = true
          authDataScope(Object.assign(values)).then(res => {
            console.log(res)
            if (res.code === 0) {
              _this.$message.success('保存成功')
              _this.$emit('ok')
              _this.visible = false
            } else {
              _this.$message.warning(res.msg)
            }
          }).catch(() => {
            _this.$message.error('系统错误，请稍后再试')
          }).finally(() => {
            _this.confirmLoading = false
          })
        }
      })
    },
    handleCancel () {
      this.visible = false
    }

  }
}
