import { STable } from '@/components'
import { getModelList, delModel, deploy } from '@/api/activiti'
import { checkPermission } from '@/utils/permissions'
const modelerBaseUrl = process.env.VUE_APP_MODEL_BASE_URL
export default {
  name: 'ModelList',
  components: {
    STable
  },
  data () {
    return {
      visible: false,
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      },
      form: this.$form.createForm(this),
      mdl: {},
      permissions: [],
      // 高级搜索 展开/关闭
      advanced: false,
      // 查询参数
      queryParam: {},
      // 表头
      columns: [
        {
          title: '编号',
          dataIndex: 'id'
        },
        {
          title: '名称',
          dataIndex: 'name'
        },
        {
          title: '版本',
          dataIndex: 'version'
        },
        {
          title: '发布编号',
          dataIndex: 'deploymentId'
        },
        {
          title: '创建时间',
          dataIndex: 'createTime'
        },
        {
          title: '最后时间',
          dataIndex: 'lastUpdateTime'
        },
        {
          title: '操作',
          width: '150px',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        return getModelList(Object.assign(parameter, this.queryParam))
      },
      modelVisible: false,
      selectedRowKeys: [],
      selectedRows: [],
      addEnable: checkPermission('activiti:models:add'),
      editEnabel: checkPermission('activiti:models:edit'),
      removeEnable: checkPermission('activiti:models:remove')
    }
  },
  computed: {
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange,
        getCheckboxProps: record => ({
          props: {
            disabled: record.deploymentId, // Column configuration not to be checked
            name: record.name
          }
        })
      }
    }
  },
  filters: {
  },
  created () {
  },
  methods: {
    onSelectChange (selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys
    },
    handleAdd () {
      this.modelVisible = true
      this.modelerUrl = modelerBaseUrl + '/models/newModel?time=' + new Date().getTime()
    },
    handleEdit (id) {
      this.modelVisible = true
      this.modelerUrl = modelerBaseUrl + '/modeler.html?modelId=' + id + '&time=' + new Date().getTime()
    },
    handleOk () {
      this.$refs.table.refresh(true)
      console.log('handleSaveOk')
    },
    dataModal (dictType) {
      this.$refs.datamodal.show(dictType)
    },
    modelCancel () {
      this.modelVisible = false
      this.handleOk()
    },
    deploy (id) {
      deploy(id).then(res => {
        if (res.code === 0) {
          this.$message.success(`发布成功`)
          this.handleOk()
        } else {
          this.$message.error(res.msg)
        }
      })
    },
    delByIds (ids) {
      delModel({ ids: ids.join(',') }).then(res => {
        if (res.code === 0) {
          this.$message.success(`删除成功`)
          this.handleOk()
        } else {
          this.$message.error(res.msg)
        }
      })
    }
  },
  watch: {
    /*
      'selectedRows': function (selectedRows) {
        this.needTotalList = this.needTotalList.map(item => {
          return {
            ...item,
            total: selectedRows.reduce( (sum, val) => {
              return sum + val[item.dataIndex]
            }, 0)
          }
        })
      }
      */
  }
}
